!(function(o) {
  "use strict";
  o(window).on("load", function() {
    o("#preloader")
      .delay(600)
      .fadeOut();
  }),
    o("body").scrollspy({ target: "#nav", offset: o(window).height() / 2 }),
    o("#nav .main-nav a[href^='#']").on("click", function(a) {
      a.preventDefault();
      this.hash;
      o("html, body").animate({ scrollTop: o(this.hash).offset().top }, 600);
    }),
    o("#back-to-top").on("click", function() {
      o("body,html").animate({ scrollTop: 0 }, 600);
    }),
    o("#nav .nav-collapse").on("click", function() {
      o("#nav").toggleClass("open");
    }),
    o(".has-dropdown a").on("click", function() {
      o(this)
        .parent()
        .toggleClass("open-drop");
    }),
    o(window).on("scroll", function() {
      var a = o(this).scrollTop();
      1 < a
        ? o("#nav").addClass("fixed-nav")
        : o("#nav").removeClass("fixed-nav"),
        700 < a ? o("#back-to-top").fadeIn() : o("#back-to-top").fadeOut();
    }),
    o(".work").magnificPopup({ delegate: ".lightbox", type: "image" }),
    o("#about-slider").owlCarousel({
      items: 1,
      loop: !0,
      margin: 15,
      nav: !0,
      navText: [
        '<i class="fa fa-angle-left"></i>',
        '<i class="fa fa-angle-right"></i>'
      ],
      dots: !0,
      autoplay: !0,
      animateOut: "fadeOut"
    }),
    o("#testimonial-slider").owlCarousel({
      loop: !0,
      margin: 15,
      dots: !0,
      nav: !1,
      autoplay: !0,
      responsive: { 0: { items: 1 }, 992: { items: 2 } }
    });
})(jQuery);
